import { NgModule } from '@angular/core';
import { RouterModule, Routes } from '@angular/router';
import { LoginComponent } from 'src/components/login/login.component';
import { MainPageComponent } from 'src/components/main-page/main-page.component';  // Importa el MainPageComponent

const routes: Routes = [
  { path: '', component: MainPageComponent },   // Ruta por defecto
  { path: 'login', component: LoginComponent }, // Ruta para login
  { path: '**', redirectTo: '', pathMatch: 'full' } // Redirige cualquier ruta no encontrada a la raíz
];

@NgModule({
  imports: [RouterModule.forRoot(routes)],
  exports: [RouterModule]
})
export class AppRoutingModule { }
