import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import * as bootstrap from 'bootstrap';

@Component({
  selector: 'app-navbar',
  template: `
    <nav class="navbar navbar-expand-lg navbar-dark bg-black py-0">
      <div class="container-fluid">
        <a class="navbar-brand py-2" href="#">
          <img src="assets/logo.png" alt="TechNova Logo" width="100" height="30">
        </a>
        <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarContent" aria-controls="navbarContent" aria-expanded="false" aria-label="Toggle navigation">
          <span class="navbar-toggler-icon"></span>
        </button>
        <div class="collapse navbar-collapse" id="navbarContent">
          <div class="d-flex flex-grow-1">
            <div class="input-group">
              <button class="btn btn-outline-light dropdown-toggle" type="button" data-bs-toggle="dropdown" aria-expanded="false">Todo</button>
              <ul class="dropdown-menu">
                <li><a class="dropdown-item" href="#">Electrónica</a></li>
                <li><a class="dropdown-item" href="#">Software</a></li>
                <li><a class="dropdown-item" href="#">Hardware</a></li>
              </ul>
              <input type="text" class="form-control" placeholder="Buscar TechNova">
              <button class="btn btn-warning" type="button">
                <i class="bi bi-search"></i>
              </button>
            </div>
          </div>
          <ul class="navbar-nav ms-auto mb-2 mb-lg-0">
            <li class="nav-item dropdown">
              <a class="nav-link dropdown-toggle text-white" href="/login" role="button" data-bs-toggle="dropdown" aria-expanded="false">
                <small>Hola, Inicia Sesión</small><br>
                <strong>Cuenta</strong>
              </a>
              <ul class="dropdown-menu">
                <li><a class="dropdown-item" (click)="navigateToLogin()">Iniciar Sesión</a></li>
              </ul>
            </li>
            <li class="nav-item" (click)="navigateToLogin()">
              <a class="nav-link text-white" href="/login">
                <small>Carrito</small><br>
                <strong>y Pedidos</strong>
              </a>
            </li>
            <li class="nav-item">
              <a class="nav-link text-white" href="#">
                <i class="bi bi-cart3 fs-4"></i>
              </a>
            </li>
          </ul>
        </div>
      </div>
    </nav>
    <div class="bg-light py-1">
      <div class="container-fluid">
        <ul class="nav">
          <li class="nav-item">
            <a class="nav-link text-dark" href="#"><i class="bi bi-list me-2"></i>Todo</a>
          </li>
          <li class="nav-item">
            <a class="nav-link text-dark" href="#">Ofertas del Día</a>
          </li>
        </ul>
      </div>
    </div>
  `,
  styles: [`
    .navbar-brand img {
      filter: brightness(0) invert(1);
    }
    .nav-link small {
      font-size: 0.75rem;
    }
    .nav-link strong {
      font-size: 0.9rem;
    }
  `]
})
export class NavbarComponent implements OnInit {
  constructor(private router: Router) {}

  ngOnInit() {
    const dropdownElementList = [].slice.call(document.querySelectorAll('.dropdown-toggle'));
    dropdownElementList.map(function (dropdownToggleEl) {
      return new bootstrap.Dropdown(dropdownToggleEl);
    });
  }

  navigateToLogin() {
    this.router.navigate(['/login']);
  }
}
