import { NgModule } from '@angular/core';
import { RouterModule, Routes } from '@angular/router';
import { LoginComponent } from 'src/components/register-login/login/login.component';
import { MainPageComponent } from 'src/components/main-page/main-page.component';  // Importa el MainPageComponent
import { RegisterComponent } from 'src/components/register-login/register/register.component';

const routes: Routes = [
  { path: 'login', component: LoginComponent },
  { path: 'register', component: RegisterComponent },
  { path: '', component: MainPageComponent },  // Página principal
  { path: '**', redirectTo: '' }  // Redirige cualquier ruta no válida a la principal
];


@NgModule({
  imports: [RouterModule.forRoot(routes)],
  exports: [RouterModule]
})
export class AppRoutingModule { }
