// main-page.component.ts
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-main-page',
  template: `
    <header class="hero-section">
      <div class="container h-100">
        <div class="row h-100 align-items-center">
          <div class="col-12 text-center">
            <h1 class="display-3 text-white mb-3">Innovating for Tomorrow</h1>
            <p class="lead text-white mb-4">Discover cutting-edge technology solutions that drive your business forward</p>
            <a href="#" class="btn btn-primary btn-lg me-3">Explore Products</a>
            <a href="#" class="btn btn-outline-light btn-lg">Our Services</a>
          </div>
        </div>
      </div>
    </header>

    <section class="features-section py-5">
      <div class="container">
        <h2 class="text-center mb-5">Why Choose TechNova?</h2>
        <div class="row">
          <div class="col-md-4 mb-4" *ngFor="let feature of features">
            <div class="card h-100 border-0 shadow-sm">
              <div class="card-body text-center">
                <i [class]="feature.icon + ' display-4 text-primary mb-3'"></i>
                <h4 class="card-title">{{feature.title}}</h4>
                <p class="card-text">{{feature.description}}</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>

    <section class="products-section py-5 bg-light">
      <div class="container">
        <h2 class="text-center mb-5">Our Latest Products</h2>
        <div class="row">
          <div class="col-md-4 mb-4" *ngFor="let product of featuredProducts">
            <div class="card h-100 border-0 shadow-sm">
              <img [src]="product.image" class="card-img-top" [alt]="product.name">
              <div class="card-body">
                <h5 class="card-title">{{product.name}}</h5>
                <p class="card-text">{{product.description}}</p>
                <a href="#" class="btn btn-outline-primary">Learn More</a>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>

    <section class="cta-section py-5 bg-primary text-white">
      <div class="container text-center">
        <h2 class="mb-4">Ready to Transform Your Business?</h2>
        <p class="lead mb-4">Let's work together to bring your ideas to life with our innovative technology solutions.</p>
        <a href="#" class="btn btn-light btn-lg">Get Started Today</a>
      </div>
    </section>

    <footer class="bg-dark text-white py-4">
      <div class="container">
        <div class="row">
          <div class="col-md-4 mb-3 mb-md-0">
            <h5>About TechNova</h5>
            <p>Innovating for a smarter tomorrow. We provide cutting-edge technology solutions to businesses worldwide.</p>
          </div>
          <div class="col-md-4 mb-3 mb-md-0">
            <h5>Quick Links</h5>
            <ul class="list-unstyled">
              <li><a href="#" class="text-white">Products</a></li>
              <li><a href="#" class="text-white">Services</a></li>
              <li><a href="#" class="text-white">About Us</a></li>
              <li><a href="#" class="text-white">Contact</a></li>
            </ul>
          </div>
          <div class="col-md-4">
            <h5>Connect With Us</h5>
            <div class="d-flex">
              <a href="#" class="text-white me-3"><i class="bi bi-facebook"></i></a>
              <a href="#" class="text-white me-3"><i class="bi bi-twitter"></i></a>
              <a href="#" class="text-white me-3"><i class="bi bi-linkedin"></i></a>
              <a href="#" class="text-white"><i class="bi bi-instagram"></i></a>
            </div>
          </div>
        </div>
        <hr class="my-4">
        <div class="text-center">
          <small>&copy; 2023 TechNova. All rights reserved.</small>
        </div>
      </div>
    </footer>
  `,
  styles: [`
    .hero-section {
      background: linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.6)), url('assets/hero-bg.jpg') no-repeat center center;
      background-size: cover;
      height: 100vh;
      display: flex;
      align-items: center;
    }
    .features-section .card {
      transition: transform 0.3s ease;
    }
    .features-section .card:hover {
      transform: translateY(-10px);
    }
    .products-section .card img {
      height: 200px;
      object-fit: cover;
    }
    .cta-section {
      background: linear-gradient(to right, #007bff, #0056b3);
    }
    footer a {
      transition: opacity 0.3s ease;
    }
    footer a:hover {
      opacity: 0.8;
    }
  `]
})
export class MainPageComponent implements OnInit {
  features = [
    { icon: 'bi bi-lightbulb', title: 'Innovative Solutions', description: 'Cutting-edge technology tailored to your needs' },
    { icon: 'bi bi-people', title: 'Expert Team', description: 'Highly skilled professionals dedicated to your success' },
    { icon: 'bi bi-shield-check', title: 'Reliable Support', description: '24/7 support to ensure your systems run smoothly' }
  ];

  featuredProducts = [
    { name: 'AI-Powered Analytics', description: 'Harness the power of artificial intelligence for data-driven insights', image: 'assets/product1.jpg' },
    { name: 'Cloud Integration Suite', description: 'Seamlessly connect and manage your cloud infrastructure', image: 'assets/product2.jpg' },
    { name: 'Cyber Security Platform', description: 'Protect your digital assets with our advanced security solutions', image: 'assets/product3.jpg' }
  ];

  ngOnInit() {
    // Aquí puedes añadir lógica adicional si es necesario
  }
}
