import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  template: `
    <div class="container mt-5">
      <div class="row justify-content-center">
        <div class="col-md-6">
          <div class="card">
            <div class="card-body">
              <h2 class="text-center mb-4">Iniciar sesión</h2>
              <form>
                <div class="mb-3">
                  <label for="email" class="form-label">Correo electrónico</label>
                  <input type="email" class="form-control" id="email" required>
                </div>
                <div class="mb-3">
                  <label for="password" class="form-label">Contraseña</label>
                  <input type="password" class="form-control" id="password" required>
                </div>
                <button type="submit" class="btn btn-warning w-100">Continuar</button>
              </form>
              <div class="mt-3 text-center">
                <small>Al continuar, aceptas las Condiciones de uso y el Aviso de privacidad de TechNova.</small>
              </div>
              <hr>
              <div class="text-center">
                <p>¿Eres nuevo en TechNova?</p>
                <button class="btn btn-light w-100" (click)="navigateToRegister()">Crear tu cuenta de TechNova</button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  `,
  styles: [`
    .card {
      border: 1px solid #ddd;
      border-radius: 8px;
    }
    .btn-warning {
      background-color: #f0c14b;
      border-color: #a88734 #9c7e31 #846a29;
    }
    .btn-light {
      background-color: #e7e9ec;
      border-color: #adb1b8 #a2a6ac #8d9096;
    }
  `]
})
export class LoginComponent {
  constructor(private router: Router) {}

  navigateToRegister() {
    this.router.navigate(['/register']);
  }
}
