import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-add-founds',
  templateUrl: './add-founds.component.html',
})
export class AddFoundsComponent implements OnInit {
  addFoundsForm: FormGroup;

  constructor(private formBuilder: FormBuilder) {
    this.addFoundsForm = this.formBuilder.group({
      digits: ['', [Validators.required, Validators.pattern(/^\d{16}$/), Validators.maxLength(16)]],
      cvv: ['', [Validators.required, Validators.pattern(/^\d{3}$/)], Validators.maxLength(3)],
      funds: ['', [Validators.required, Validators.min(1)], Validators.maxLength(10)],
    });
  }

  ngOnInit(): void {}

  onSubmit(): void {
    if (this.addFoundsForm.valid) {
      const cardData = this.addFoundsForm.value;
      console.log('Card Data:', cardData);
    }
  }
}
