// navbar.component.ts
import { Component } from '@angular/core';

@Component({
  selector: 'app-navbar',
  template: `
    <nav class="navbar navbar-expand-lg navbar-dark bg-dark py-3">
      <div class="container">
        <a class="navbar-brand" href="#">
          <img src="assets/logo.png" alt="TechNova Logo" width="40" height="40" class="d-inline-block align-text-top me-2">
          TechNova
        </a>
        <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
          <span class="navbar-toggler-icon"></span>
        </button>
        <div class="collapse navbar-collapse" id="navbarNav">
          <ul class="navbar-nav me-auto mb-2 mb-lg-0">
            <li class="nav-item">
              <a class="nav-link" routerLink="/" routerLinkActive="active" [routerLinkActiveOptions]="{exact: true}">Home</a>
            </li>
            <li class="nav-item dropdown">
              <a class="nav-link dropdown-toggle" href="#" id="productsDropdown" role="button" data-bs-toggle="dropdown" aria-expanded="false">
                Products
              </a>
              <ul class="dropdown-menu" aria-labelledby="productsDropdown">
                <li><a class="dropdown-item" routerLink="/products/software">Software Solutions</a></li>
                <li><a class="dropdown-item" routerLink="/products/hardware">Hardware Innovations</a></li>
                <li><a class="dropdown-item" routerLink="/products/ai">AI & Machine Learning</a></li>
              </ul>
            </li>
            <li class="nav-item dropdown">
              <a class="nav-link dropdown-toggle" href="#" id="servicesDropdown" role="button" data-bs-toggle="dropdown" aria-expanded="false">
                Services
              </a>
              <ul class="dropdown-menu" aria-labelledby="servicesDropdown">
                <li><a class="dropdown-item" routerLink="/services/consulting">Tech Consulting</a></li>
                <li><a class="dropdown-item" routerLink="/services/development">Custom Development</a></li>
                <li><a class="dropdown-item" routerLink="/services/support">24/7 Support</a></li>
              </ul>
            </li>
            <li class="nav-item">
              <a class="nav-link" routerLink="/about" routerLinkActive="active">About Us</a>
            </li>
            <li class="nav-item">
              <a class="nav-link" routerLink="/contact" routerLinkActive="active">Contact</a>
            </li>
          </ul>
          <form class="d-flex">
            <input class="form-control me-2" type="search" placeholder="Search products or services" aria-label="Search">
            <button class="btn btn-outline-light" type="submit">Search</button>
          </form>
          <ul class="navbar-nav ms-auto">
            <li class="nav-item">
              <a class="nav-link" href="#"><i class="bi bi-person-circle me-1"></i>Login</a>
            </li>
          </ul>
        </div>
      </div>
    </nav>
  `,
  styles: [`
    .navbar-brand {
      font-weight: bold;
      font-size: 1.5rem;
    }
    .nav-link {
      font-size: 1.1rem;
      transition: color 0.3s ease;
    }
    .navbar-nav .nav-link.active,
    .navbar-nav .nav-link:hover {
      color: #17a2b8;
    }
    .dropdown-menu {
      border: none;
      box-shadow: 0 0.5rem 1rem rgba(0, 0, 0, 0.15);
    }
    .dropdown-item:hover {
      background-color: #f8f9fa;
    }
    @media (max-width: 991.98px) {
      .navbar-nav {
        padding-top: 1rem;
      }
      .navbar-nav .nav-item {
        padding: 0.5rem 0;
      }
    }
  `]
})
export class NavbarComponent { }
